import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './App.css';

function Login() {
    const [username, setUsername] = useState(''); // 用户名状态
    const [password, setPassword] = useState(''); // 密码状态
    const [loginStatus, setLoginStatus] = useState(null); // 状态，用于处理登录状态
    const navigate = useNavigate(); // 导航函数

    const handleSubmit = async (e) => {
        e.preventDefault(); // 阻止表单默认提交行为
        const response = await fetch('http://127.0.0.1:4523/m1/4584133-4233190-default/api/login?apifoxApiId=180056105', {
            method: 'POST', // 请求方法
            headers: {
                'Content-Type': 'application/json', // 设置请求头，指定发送的数据类型为 JSON
            },
            body: JSON.stringify({ username, password }), // 请求体，发送用户名和密码
        });

        const result = await response.json(); // 假设后端返回一个包含布尔属性的 JSON 对象

        if (response.ok && result.success) { // 检查成功布尔值
            // 登录成功
            setLoginStatus('登录成功');
            // 可选地，在延迟后导航到另一个页面
            setTimeout(() => {
                navigate('/'); // 导航到主页
            }, 2000);
        } else {
            // 处理登录错误
            setLoginStatus('登录失败：用户名或密码无效');
            // 可选地，在延迟后导航到另一个页面
            setTimeout(() => {
                navigate('/'); // 导航到主页
            }, 2000);
        }
    };

    return (
        <div className="container">
            <h2>登录</h2>
            <form onSubmit={handleSubmit}> {/* 提交表单时调用 handleSubmit 函数 */}
                <input
                    type="text"
                    placeholder="用户名"
                    value={username}
                    onChange={(e) => setUsername(e.target.value)} // 更新用户名状态
                    required
                />
                <input
                    type="password"
                    placeholder="密码"
                    value={password}
                    onChange={(e) => setPassword(e.target.value)} // 更新密码状态
                    required
                />
                <button type="submit">登录</button>
            </form>
            {loginStatus && <p>{loginStatus}</p>} {/* 显示登录状态消息 */}
            <button onClick={() => navigate('/')}>返回</button>
        </div>
    );
}

export default Login;
