import React, { useState } from 'react';
import SingleChoice from './SingleChoice';
import MultipleChoice from './MultipleChoice';
import TextQuestion from './TextQuestion';
import './NewSurvey.css';

function NewSurvey() {
    const [questions, setQuestions] = useState([]);

    const addQuestion = (type) => {
        const newQuestion = { type, id: Date.now() };
        setQuestions([...questions, newQuestion]);
    };

    return (
        <div className="new-survey">
            <aside className="sidebar">
                <h2>题目类型</h2>
                <button onClick={() => addQuestion('single')}>单选题</button>
                <button onClick={() => addQuestion('multiple')}>多选题</button>
                <button onClick={() => addQuestion('text')}>填空题</button>
            </aside>
            <div className="content">
                {questions.map((question) => {
                    switch (question.type) {
                        case 'single':
                            return <SingleChoice key={question.id} />;
                        case 'multiple':
                            return <MultipleChoice key={question.id} />;
                        case 'text':
                            return <TextQuestion key={question.id} />;
                        default:
                            return null;
                    }
                })}
            </div>
        </div>
    );
}

export default NewSurvey;
