import React, { useState } from 'react';

function MultipleChoice() {
    const [options, setOptions] = useState(['']);
    const [question, setQuestion] = useState('');

    const addOption = () => {
        setOptions([...options, '']);
    };

    const handleOptionChange = (index, value) => {
        const newOptions = options.map((option, i) => i === index ? value : option);
        setOptions(newOptions);
    };

    return (
        <div>
            <input
                type="text"
                placeholder="请输入多选题题目"
                value={question}
                onChange={(e) => setQuestion(e.target.value)}
                style={{ width: '100%', padding: '15px', marginBottom: '15px', fontSize: '18px' }}
            />
            {options.map((option, index) => (
                <div key={index}>
                    <input
                        type="text"
                        placeholder={`选项 ${index + 1}`}
                        value={option}
                        onChange={(e) => handleOptionChange(index, e.target.value)}
                        style={{ width: '100%', padding: '15px', marginBottom: '15px', fontSize: '18px' }}
                    />
                </div>
            ))}
            <button onClick={addOption} style={{ padding: '15px', fontSize: '18px' }}>增加选项</button>
        </div>
    );
}

export default MultipleChoice;
