import React, { useState } from 'react';
import SingleChoiceQuestion from './SingleChoice';
import MultipleChoiceQuestion from './MultipleChoice';
import TextQuestion from './TextQuestion';

const NewSurvey = () => {
    const [questions, setQuestions] = useState([]);

    // 添加问题的函数
    const addQuestion = (type) => {
        setQuestions([...questions, { id: Date.now(), type }]);
    };

    // 删除问题的函数
    const deleteQuestion = (id) => {
        setQuestions(questions.filter(q => q.id !== id));
    };

    // 渲染问题的函数
    const renderQuestion = (question) => {
        switch (question.type) {
            case 'SingleChoice':
                return <SingleChoiceQuestion key={question.id} id={question.id} deleteQuestion={deleteQuestion} />;
            case 'MultipleChoice':
                return <MultipleChoiceQuestion key={question.id} id={question.id} deleteQuestion={deleteQuestion} />;
            case 'Text':
                return <TextQuestion key={question.id} id={question.id} deleteQuestion={deleteQuestion} />;
            default:
                return null;
        }
    };

    return (
        <div style={{ display: 'flex', height: '100vh' }}>
            <div style={{ width: '20%', background: '#f0f0f0', padding: '10px' }}>
                <h3>调查问卷构建器</h3>
                <button onClick={() => addQuestion('SingleChoice')}>添加单选题</button>
                <button onClick={() => addQuestion('MultipleChoice')}>添加多选题</button>
                <button onClick={() => addQuestion('Text')}>添加文本题</button>
            </div>
            <div style={{ width: '80%', overflowY: 'auto', padding: '10px' }}>
                {questions.map(renderQuestion)}
            </div>
        </div>
    );
};

export default NewSurvey;
